	/**
	 * コードポイントから国旗（Regional Indicator）を構成する文字の判定
	 *
	 * @param {number} codepoint - コードポイント
	 * @returns {boolean} 確認結果
	 */
	static isRegionalIndicatorFromCodePoint(codepoint) {
		// prettier-ignore
		return (0x1F1E6 <= codepoint && codepoint <= 0x1F1FF);
