			0xFF79:	0x30B1,	//	ケ	ｹ
			0xFF7A:	0x30B3,	//	コ	ｺ
			0xFF7B:	0x30B5,	//	サ	ｻ
			0xFF7C:	0x30B7,	//	シ	ｼ
