			0xFF80:	0x30BF,	//	タ	ﾀ
			0xFF81:	0x30C1,	//	チ	ﾁ
			0xFF82:	0x30C4,	//	ツ	ﾂ
			0xFF83:	0x30C6,	//	テ	ﾃ
			0xFF84:	0x30C8,	//	ト	ﾄ
			0xFF85:	0x30CA,	//	ナ	ﾅ
			0xFF86:	0x30CB,	//	ニ	ﾆ
			0xFF87:	0x30CC,	//	ヌ	ﾇ
			0xFF88:	0x30CD,	//	ネ	ﾈ
			0xFF89:	0x30CE,	//	ノ	ﾉ
			0xFF8A:	0x30CF,	//	ハ	ﾊ
			0xFF8B:	0x30D2,	//	ヒ	ﾋ
			0xFF8C:	0x30D5,	//	フ	ﾌ
			0xFF8D:	0x30D8,	//	ヘ	ﾍ
			0xFF8E:	0x30DB,	//	ホ	ﾎ
			0xFF8F:	0x30DE,	//	マ	ﾏ
			0xFF90:	0x30DF,	//	ミ	ﾐ
			0xFF91:	0x30E0,	//	ム	ﾑ
			0xFF92:	0x30E1,	//	メ	ﾒ
			0xFF93:	0x30E2,	//	モ	ﾓ
			0xFF94:	0x30E4,	//	ヤ	ﾔ
			0xFF95:	0x30E6,	//	ユ	ﾕ
			0xFF96:	0x30E8,	//	ヨ	ﾖ
			0xFF97:	0x30E9,	//	ラ	ﾗ
			0xFF98:	0x30EA,	//	リ	ﾘ
			0xFF99:	0x30EB,	//	ル	ﾙ
			0xFF9A:	0x30EC,	//	レ	ﾚ
			0xFF9B:	0x30ED,	//	ロ	ﾛ
			0xFF9C:	0x30EF,	//	ワ	ﾜ
			0xFF9D:	0x30F3,	//	ン	ﾝ
			0xFF9E:	0x309B,	//	゛	ﾞ
			0xFF9F:	0x309C		//	゜	ﾟ
		};
		/**
		 * @param {string} str
		 */
		const func = function (str) {
			if (str.length === 1) {
				return String.fromCharCode(map[str.charCodeAt(0)]);
			} else {
				const next = str.charCodeAt(1);
				const ch = str.charCodeAt(0);
				if (next === 0xFF9E) {
					// Shift-JISにない濁点（ヷ、ヸ、ヹ、ヺ）は意図的に無視
					if (ch === 0xFF73) {
						// ヴ
						return String.fromCharCode(0x3094);
					} else if ((0xFF76 <= ch && ch <= 0xFF84) || (0xFF8A <= ch && ch <= 0xFF8E)) {
						// ガ-ド、バ-ボ
						return String.fromCharCode(map[ch] + 1);
					}
				} else if (next === 0xFF9F) {
					// 半濁点
					if (0xFF8A <= ch && ch <= 0xFF8E) {
					// パ-ポ
						return String.fromCharCode(map[ch] + 2);
					}
				}
				return String.fromCharCode(map[ch]) + String.fromCharCode(map[next]);
			}
		};
		return text.replace(/[\uFF61-\uFF9F][\uFF9E\uFF9F]?/g, func);
	}

